mapper.insert:

	SELECT NVL(MAX(COST_NO),0)+1
	FROM FORWIZ_COST_TB

INSERT INTO FORWIZ_COST_TB (COST_NO
					  	  , COST_DATE
						  , COST_METHOD
						  , COST_CATEGORY
						  , COST_DETAIL
						  , COST_BUYER
						  , COST_SUM
						  , FILE_NAME
						  , FILE_RENAME
						  , USER_ID)
VALUES (#{costNo}
	  , #{costDate}
	  , #{costMethod}
	  , #{costCategory}
	  , #{costDetail}
	  , #{costBuyer}
	  , #{costSum}
	  , #{fileName}
	  , #{fileRename}
	  , #{userId})

mapper.update:
UPDATE FORWIZ_COST_TB
COST_METHOD=#{costMethod}, COST_CATEGORY=#{costCategory}, COST_DETAIL=#{costDetail}, COST_SUM=#{costSum}, FILE_NAME=NVL(#{fileName},FILE_NAME), FILE_RENAME=NVL(#{fileRename},FILE_RENAME),
WHERE COST_NO = #{costNo}
