mapper.update:
UPDATE FREE_BULLETIN
   SET FREE_HIT = FREE_HIT + 1
 WHERE FREE_NO = #{freeNo}

mapper.insert:
		INSERT INTO FREE_BULLETIN_REPLY(FREE_NO
						  			  , REPLY_NO
						  			  , REPLY_PARENT_NO
						  			  , REPLY_CONTENT
						  			  , REPLY_WRITER
)
		VALUES (#{freeNo}
			  , FREE_RE_SEQ.NEXTVAL
			  , #{replyParentNo}
			  , #{replyContent}
			  , #{replyWriter})

mapper.update:
UPDATE FREE_BULLETIN_REPLY
REPLY_CONTENT = #{replyContent}, REPLY_UPDATE_DATE = SYSDATE,
WHERE REPLY_NO = #{replyNo}

UPDATE FREE_BULLETIN_REPLY
REPLY_DELETE_AT = #{replyDeleteAt}, REPLY_DELETE_DATE = SYSDATE,
WHERE REPLY_NO = #{replyNo}
