mapper.select:
UPDATE MOVIES
   SET MOVIE_HIT = NVL(MOVIE_HIT,0) + 1
 WHERE MOVIE_NO = #{movieNo}

mapper.insert:
INSERT INTO MOVIE_REPLY(MOVIE_REPLY_NO
					  , MOVIE_REPLY_CONENT
					  , MOVIE_REPLY_WRITER
					  , MOVIE_NO
					  , MOVIE_REPLY_STAR)
VALUES (MV_RE_SEQ.NEXTVAL
	  , #{movieReplyConent}
	  , #{movieReplyWriter}
	  , #{movieNo}
	  , #{movieReplyStar})

mapper.select:
SELECT NVL(AVG(MOVIE_REPLY_STAR),0) "REPLY_AVG"
  FROM MOVIE_REPLY
 WHERE MOVIE_NO = #{movieNo}
