mapper.insert:
INSERT INTO USERS(USER_NAME
				, USER_ID
				, USER_PWD
				, USER_ROLE
				, USER_GENDER
				, USER_TEL)
VALUES(#{userName}
	 , #{userId}
	 , #{userPwd}
	 , 'user'
	 , #{userGender}
	 , #{userTel})

mapper.delete:
DELETE FROM USERS
WHERE USER_ID = #{userId}

	AND USER_PWD = #{userPwd}
