mapper.insert:

	SELECT NVL(MAX(MOVIE_NO),0) + 1
	  FROM MOVIES

INSERT INTO MOVIES (MOVIE_NO
				  , MOVIE_NAME
				  , MOVIE_CONENT
				  , MOVIE_HIT
				  , MOVIE_DIRECTOR
				  , MOVIE_ACTOR
				  , MOVIE_PRICE)
VALUES (#{movieNo}
	  , #{movieName}
	  , #{movieConent}
	  , 0
	  , #{movieDirector}
	  , #{movieActor}
	  , #{moviePrice})

mapper.select:
UPDATE MOVIES
   SET MOVIE_HIT = NVL(MOVIE_HIT,0) + 1
 WHERE MOVIE_NO = #{movieNo}
